import { Bar, Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    BarElement,
    CategoryScale,
    LinearScale,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { format, parseISO, eachDayOfInterval, startOfMonth, endOfMonth } from 'date-fns';

ChartJS.register(
    BarElement,
    CategoryScale,
    LinearScale,
    ArcElement,
    Tooltip,
    Legend
);

function SummaryChart({ expenses = [] }) {
    if (!expenses || expenses.length === 0) {
        return (
            <div className="summary-chart">
                <h2>Spending Analysis</h2>
                <p>No data to display</p>
            </div>
        );
    }

    // Current month data for bar chart
    const currentMonth = format(new Date(), 'yyyy-MM');
    const currentMonthExpenses = expenses.filter(exp =>
        format(parseISO(exp.date), 'yyyy-MM') === currentMonth
    );

    // Bar Chart Data
    const barChartData = {
        labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
        datasets: [{
            label: 'Weekly Spending ($)',
            data: [150, 230, 180, 210], // Replace with your actual weekly data
            backgroundColor: '#4bc0c0',
            borderColor: '#4bc0c0',
            borderWidth: 1
        }]
    };

    // Pie Chart Data
    const pieChartData = {
        labels: ['Housing', 'Food', 'Transport', 'Entertainment'],
        datasets: [{
            data: [45, 30, 15, 10], // Replace with your actual category percentages
            backgroundColor: [
                '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'
            ]
        }]
    };

    return (
        <div className="summary-chart">
            <h2>Spending Analysis</h2>

            <div className="chart-container">
                {/* Bar Chart - Weekly Spending */}
                <div className="chart-section">
                    <h3>Weekly Spending This Month</h3>
                    <div className="chart-wrapper">
                        <Bar
                            data={barChartData}
                            options={{
                                responsive: true,
                                scales: {
                                    y: {
                                        beginAtZero: true,
                                        title: {
                                            display: true,
                                            text: 'Amount ($)'
                                        }
                                    },
                                    x: {
                                        title: {
                                            display: true,
                                            text: 'Weeks of Month'
                                        }
                                    }
                                }
                            }}
                        />
                    </div>
                </div>

                {/* Pie Chart - Category Breakdown */}
                <div className="chart-section">
                    <h3>Spending by Category</h3>
                    <div className="chart-wrapper">
                        <Pie
                            data={pieChartData}
                            options={{
                                plugins: {
                                    tooltip: {
                                        callbacks: {
                                            label: (context) => {
                                                const label = context.label || '';
                                                const value = context.raw || 0;
                                                const total = context.dataset.data.reduce((a, b) => a + b, 0);
                                                const percentage = Math.round((value / total) * 100);
                                                return `${label}: $${value} (${percentage}%)`;
                                            }
                                        }
                                    }
                                }
                            }}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SummaryChart;
