import { Bar, Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    BarElement,
    CategoryScale,
    LinearScale,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { format, parseISO, eachDayOfInterval, startOfMonth, endOfMonth } from 'date-fns';

// Register ChartJS components
ChartJS.register(
    BarElement,
    CategoryScale,
    LinearScale,
    ArcElement,
    Tooltip,
    Legend
);

function SummaryChart({ expenses = [] }) {
    if (!expenses || expenses.length === 0) {
        return (
            <div className="summary-chart">
                <h2>Spending Analysis</h2>
                <p>No data to display</p>
            </div>
        );
    }

    // Current month's expenses
    const currentMonth = format(new Date(), 'yyyy-MM');
    const currentMonthExpenses = expenses.filter(exp =>
        format(parseISO(exp.date), 'yyyy-MM') === currentMonth
    );

    // 1. Monthly Trend Chart (Bar Chart)
    const monthlyData = (() => {
        const daysInMonth = eachDayOfInterval({
            start: startOfMonth(new Date()),
            end: endOfMonth(new Date())
        });

        const dailyTotals = daysInMonth.map(day => {
            const dayStr = format(day, 'yyyy-MM-dd');
            return currentMonthExpenses
                .filter(exp => exp.date === dayStr)
                .reduce((sum, exp) => sum + exp.amount, 0);
        });

        return {
            labels: daysInMonth.map(day => format(day, 'd')),
            datasets: [{
                label: 'Daily Spending ($)',
                data: dailyTotals,
                backgroundColor: '#4bc0c0',
                borderColor: '#4bc0c0',
                borderWidth: 1
            }]
        };
    })();

    // 2. Category Breakdown (Pie Chart - kept but improved)
    const categoryData = (() => {
        const categoryTotals = expenses.reduce((acc, expense) => {
            acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
            return acc;
        }, {});

        return {
            labels: Object.keys(categoryTotals),
            datasets: [{
                data: Object.values(categoryTotals),
                backgroundColor: [
                    '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0',
                    '#9966FF', '#FF9F40', '#8AC24A'
                ]
            }]
        };
    })();

    return (
        <div className="summary-chart">
            <h2>Spending Analysis</h2>

            <div className="chart-container">
                <div className="chart-section">
                    <h3>Daily Spending This Month</h3>
                    <Bar
                        data={monthlyData}
                        options={{
                            responsive: true,
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    title: {
                                        display: true,
                                        text: 'Amount ($)'
                                    }
                                },
                                x: {
                                    title: {
                                        display: true,
                                        text: 'Day of Month'
                                    }
                                }
                            }
                        }}
                    />
                </div>

                <div className="chart-section">
                    <h3>Spending by Category</h3>
                    <Pie
                        data={categoryData}
                        options={{
                            plugins: {
                                tooltip: {
                                    callbacks: {
                                        label: (context) => {
                                            const label = context.label || '';
                                            const value = context.raw || 0;
                                            const total = context.dataset.data.reduce((a, b) => a + b, 0);
                                            const percentage = Math.round((value / total) * 100);
                                            return `${label}: $${value.toFixed(2)} (${percentage}%)`;
                                        }
                                    }
                                }
                            }
                        }}
                    />
                </div>
            </div>
        </div>
    );
}

export default SummaryChart;
